INSERT INTO `resolution` VALUES ('1884', '1888', '本题考核支付结算的主要法律依据。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1885', '1889', '单位和个人凭已承兑的商业汇票、债券、存单等付款人债务证明办理款项的结算，均可以使用委托收款结算方式。商业汇票包括商业承兑汇票和银行承兑汇票。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1886', '1890', '银行卡按照发行主体是否在境内分为境内卡、境外卡。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1887', '1891', '汇票保证必须记载的事项包括：（1）表明“保证”的字样；（2）保证人名称和住所；（3）被保证人的名称；（4）保证日期；（5）保证人签章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1888', '1892', '本题考核商业汇票的分类。商业汇票按照承兑人的不同分为商业承兑汇票和银行承兑汇票。', null, null, null, null, null, null, null);
