INSERT INTO `resolution` VALUES ('1890', '1894', '本题考核票据的签章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1891', '1895', '本题考核异地银行结算账户的适用范围。存款人有下列情形的，可以申请开立异地银行结算账户：（1）营业执照注册地与经营地不在同一行政区域需要开立基本存款账户的；（2）办理异地借款需要开立一般存款账户的；（3）存款人因附属的非独立核算单位发生的收入汇缴或业务支出需要开立专用存款账户的；（4）异地临时经营活动需要开立临时存款账户的；（5）自然人根据需要在异地开立个人银行结算账户的。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1892', '1896', '本题考核个人银行结算账户的实际功能。选项ABCD均为个人银行结算账户的实际功能。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1893', '1897', '本题考核临时存款账户的开立范围。选项C，基本建设资金应开立专用存款账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1894', '1898', '本题考核专用存款账户的开立。根据规定，本题四个选项都可以申请开立专用存款账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1895', '1899', '选项B为个人，不得开立基本存款账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1896', '1900', '一般存款账户可以办理现金缴存但不得办理现金支取。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1897', '1901', '本题考核基本存款账户的使用范围。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1898', '1902', '本题考核银行结算账户的种类。银行结算账户按用途不同可分为基本存款账户、一般存款账户、专用存款账户、临时存款账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1899', '1903', '本题考核办理支付结算的基本要求。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1900', '1904', '本题考核国内信用证的办理基本程序。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1901', '1905', '托收承付凭证记载事项有:（1）表明“托收承付”的字样；（2）确定的金额；（3）付款人的名称和账号；（4）收款人的名称和账号；（5）付款人的开户银行名称；（6）收款人的开户银行名称；（7）托收附寄单证张数或册数；（8）合同名称、号码；（9）委托日期；（10）收款人签章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1902', '1906', '本题考核汇兑的分类。汇兑分为信汇和电汇两种。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1903', '1907', '票据背书转让时，由背书人在票据背面签章、记载被背书人名称和背书日期。背书未记载日期的，视为在票据到期日前背书。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1904', '1908', '本题考查汇票的背书。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1905', '1909', '本题考核商业汇票的基本知识。商业汇票的提示付款期限，为自汇票到期日起10日内，而不是说的承兑期限，所以选项A错误。出票后定期付款的商业汇票，持票人应该在汇票到期日前向付款人提示承兑，所以选项D错误。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1906', '1910', '付款人在办理承兑手续时，应在汇票上记载承兑的事项包括承兑文句、承兑日期、承兑人签章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1907', '1911', '本题考查银行承兑汇票的出票人。在银行开立存款账户的法人以及其他组织之间，必须具有真实的交易关系或债权债务关系，与承兑银行具有真实的委托付款关系,资信状况良好，具有支付汇票金额的可靠资金来源，才能使用银行承兑汇票。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1908', '1912', '本题考核支票签发的要求。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1909', '1913', '异地之间也可使用支票进行支付结算。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1910', '1914', '本题考核票据权利。票据权利是指票据持票人向票据债务人请求支付票据金额的权利，包括付款请求权和追索权。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1911', '1915', '本题考核票据的分类。我国《票据法》上所称的票据包括汇票、本票和支票。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1912', '1916', '本题考核票据的种类。在我国，票据包括银行汇票、商业汇票、银行本票、支票。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1913', '1917', '本题考核存款人违反账户管理制度的处罚。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1914', '1918', '本题考核银行结算账户的变更。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1915', '1919', '本题考核票据的记载事项。票据金额、日期、收款人名称不得更改，更改的票据无效。对票据上的其他记载事项，原记载人可以更改，更改时应当由原记载人签章证明。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1916', '1920', '本题考核办理支付结算的基本要求。单位、个人和银行办理支付结算，必须使用中国人民银行统一规定的票据和结算凭证。票据和结算凭证是办理支付结算的工具。未使用按中国人民银行统一规定印制的票据，票据无效；未使用按中国人民银行统一规定格式的结算凭证，银行不予受理。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1917', '1921', '本题考核支付结算的特征。支付结算的特征包括：（1）必须通过中国人民银行批准的金融机构进行；（2）是一种要式行为；（3）其发生取决于委托人的意志；（4）实行统一领导、分级管理；（5）必须依法进行。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1918', '1922', '本题考核支付结算凭证填写的要求。票据出票日期使用小写填写的，银行不予受理。大写日期未按要求规范填写的，银行可予受理，但由此造成损失的，由出票人自行承担。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1919', '1923', '本题考核汇入处理。', null, null, null, null, null, null, null);
