INSERT INTO `resolution` VALUES ('1925', '1931', '本题考查商业汇票的概念，商业汇票是指出票人签发的，委托付款人在指定日期无条件支付确定的金额给收款人或者持票人的票据。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1926', '1932', '本题考核划线支票的用途。根据规定，在普通支票左上角划两条平行线的，为划线支票，划线支票只能用于转账，不能用于支取现金。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1927', '1933', '本题考核票据的出票。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1928', '1934', '本题考核异地银行结算账户。自然人根据需要在异地开立的个人银行结算账户，也属于异地银行结算账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1929', '1935', '本题考核个人银行结算账户的业务范围。个人银行结算账户可用于办理个人转账收付和现金支取。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1930', '1936', '本题考核临时存款账户。临时存款账户有效期最长不得超过2年。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1931', '1937', '本题考核专用存款账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1932', '1938', '本题考核一般存款账户和基本存款账户的使用范围。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1933', '1939', '本题考核基本存款账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1934', '1940', '本题考核银行结算账户的概念。银行结算账户仅仅包括活期存款账户，不包括定期存款账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1935', '1941', '本题考核银行不垫款原则。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1936', '1942', '本题考核国内信用证的办理基本程序。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1937', '1943', '本题考核汇票背书记载事项。根据规定，被背书人名称是背书的绝对记载事项。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1938', '1944', '本题考核信用卡的相关规定。个人卡的主卡持卡人可为其配偶及年满18周岁的亲属申领附属卡，附属卡最多不得超过两张，主卡持卡人有权要求注销其附属卡。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1939', '1945', '签发现金银行汇票，申请人和收款人必须均为个人，收妥申请人交存的现金后，在银行汇票“出票金额”栏先填写“现金”字样，后填写出票金额，并填写代理付款人名称。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1940', '1946', '持票人未按规定期限提示付款的，在作出说明后，承兑人或付款人仍应当继续对持票人承担付款责任，通过委托收款或票据交换系统向付款人付款的，视同持票人提示付款。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1941', '1947', '付款人承兑商业汇票，不得附有条件，承兑附有条件的，视为拒绝承兑。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1942', '1948', '“付款日期”属于商业汇票的相对记载事项，汇票上未记载付款日期的，为见票即付。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1943', '1949', '支票的出票人预留银行签章是银行审核支票付款的依据。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1944', '1950', '本题考核支票的签章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1945', '1951', '持票人可以根据自己的意愿，选择任意一个或多个甚至全体票据债务人进行追索。这种选择不受数量限制，也不受顺序限制，只要是持票人认为最有利于保障其权利的就可以要求追索。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1946', '1952', '本题考核票据的当事人。票据上的非基本当事人在各种票据行为中都有自己特定的名称，所以，同一当事人可以有两个名称，即双重身份。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1947', '1953', '本题考核票据的概念。票据是由出票人签发的，约定自己或者委托付款人在见票时或指定的日期向收款人或持票人无条件支付一定金额的有价证券。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1948', '1954', '本题考核银行结算账户的撤销。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1949', '1955', '本题考核办理支付结算的基本要求。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1950', '1956', '财政预算外资金、证券交易结算资金、期货交易保证金和信托基金专用存款账户不得支取现金。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1951', '1957', '本题考核现金管理。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1952', '1958', '本题考核支付结算的概念。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1953', '1959', '本题考核支付结算的中介机构。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1954', '1960', '本题考核税务代理的法定业务范围。根据该规定，纳税人、扣缴义务人可以将下列涉税业务委托税务代理人办理：（1）税务登记、变更税务登记和注销税务登记；（2）发票领购手续；（3）纳税申报和扣缴税款报告；（4）缴纳税款和申请退税；（5）制作涉税文书；（6）审查纳税情况；（7）建帐建制，办理账务；（8）申请税务行政复议或税务行政诉讼；（9）税务咨询，受聘税务顾问；（10）国家税务总局规定的其他业务。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1955', '1961', '邮寄申报以寄出地的邮戳日期为实际申报日期。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1956', '1962', '本题考核变更税务登记的概念。变更税务登记是指纳税人在办理税务登记后，原登记内容发生变化时向原税务机关申报办理税务登记。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1957', '1963', '本题考核劳务报酬的知识点。根据规定，劳务报酬所得是指个人独立从事非雇佣的各种劳务所取得的所得。包括：翻译、审稿、书画、咨询、讲学等29项具体项目。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1958', '1964', '本题考核个人所得税的纳税义务人。个人所得税的纳税人包括中国公民、个体工商户、个人独资企业投资者、合伙企业合伙人，也包括外籍人员。有限责任公司缴纳企业所得税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1959', '1965', '本题考核企业所得税的纳税申报。企业按月或按季预缴企业所得税的，应当自月份或者季度终了之日起15日内，向税务机关报送预缴企业所得税纳税申报表，预缴税款。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1960', '1966', '个人独资企业缴纳个人所得税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1961', '1967', '本题考核消费税的税目。我国消费税税目共有14个，即烟，酒与酒精，化妆品，贵重首饰及珠宝玉石，鞭炮、焰火，成品油，汽车轮胎，小汽车，摩托车，高尔夫球及球具，高档手表，游艇，木制一次性筷子，实木地板。选项B网球及球具不属于消费税税目。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1962', '1968', '本题考核营业税的征税范围。提供修理劳务属于增值税的应税行为，应缴纳增值税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1963', '1969', '本题考核营业税的纳税人。选项A，销售小汽车的销售公司属于增值税的纳税人。', null, null, null, null, null, null, null);
