INSERT INTO `resolution` VALUES ('1965', '1971', '本题考核增值税的类型。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1966', '1972', '本题考核税收的特征。税收具有强制性、无偿性 、固定性三个特征。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1967', '1973', '本题考核批发、零售应税消费品的税率。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1968', '1974', '税务机关责令具有税法规定情形的纳税人提供纳税担保而纳税人拒绝提供纳税担保或无力提供纳税担保的，经县以上税务局（分局）局长批准，税务机关可以采取税收保全措施。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1969', '1975', '当事人对税务机关的处罚决定逾期不申请行政复议，也不向人民法院起诉，又不履行的，作出处罚决定的税务机关可以采取强制性措施，或者申请人民法院强制执行。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1970', '1976', '《中华人民共和国税收征收管理法》规定纳税人有下列情形之一的，税务机关有权核定其应纳税额：1.依照法律、行政法规的规定可以不设置账簿的；2.依照法律、行政法规的规定应当设置但未设置账簿的；3.擅自销毁账簿或者拒不提供纳税资料的；4.虽设置账簿，但账目混乱或者成本资料、收入凭证、费用凭证残缺不全，难以查账的；5.发生纳税义务，未按照规定的期限办理纳税申报，经税务机关责令限期申报，逾期仍不申报的；6.纳', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1971', '1977', '本题考核增值税专用发票。增值税专用发票由国家税务总局指定的企业统一印制。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1972', '1978', '年所得12万元以上的应当按照规定到主管税务机关自行申报纳税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1973', '1979', '本题考核个人所得税的计算。特许权使用费收入“按次征收”，应纳个人所得税＝（3000－800）×20％＋4500×（1－20％）×20％＝1160（元）。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1974', '1980', '本题考核企业所得税的扣除项目。企业发生的公益性捐赠支出，在年度利润总额12％以内的部分，准予在计算应纳税所得额时扣除。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1975', '1981', '本题考核消费税的纳税期限。纳税人以1个月或者1个季度为1个纳税期的，自期满之日起15日内申报纳税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1976', '1982', '啤酒实行从量定额征税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1977', '1983', '本题考核营业税的纳税地点。纳税人转让、出租土地使用权，应当向土地所在地的主管税务机关申报纳税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1978', '1984', '本题考核营业税的计算。根据规定，娱乐业的营业额为经营娱乐业向顾客收取的各项费用。应缴纳的营业税=（60+40+50）×20%＝30（万元）。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1979', '1985', '本题考核增值税的纳税期限。纳税人以1个月或者1个季度为1个纳税期的，自期满之日起15日内申报纳税；以1日、3日、5日、10日或者15日为1个纳税期的，自期满之日起5日内预缴税款，于次月1日起15日内申报纳税并结清上月应纳税款。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1980', '1986', '本题考核销售额包含的内容。增值税是价外税，销售额不包括向购买方收取的销项税额。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1981', '1987', '本题考核增值税的税率。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1982', '1988', '本题考核增值税的纳税人。目前，认定小规模纳税人的具体年应税销售额的标准为：（1）从事货物生产或者提供应税劳务的纳税人，以及以从事货物生产或者提供应税劳务为主，并兼营货物批发或者零售的纳税人，年应税销售额在50万元以下的。（2）除上述规定以外的纳税人，年应税销售额在80万元以下的。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1983', '1989', '本题考核税法的构成要素。税目是税法中规定的征税对象的具体项目，是征税的具体根据，它规定了征税对象的具体范围。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1984', '1990', '本题考核税法的分类。选项ABD均属于税收实体法。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1985', '1991', '国家通过税种设置，以及加成征收或减免等手段来影响社会成员的经济利益，改变社会财富分配状况，对资源配置和社会经济发展产生影响，调节生产、交换、分配和消费，从而达到调控经济运行的目的，促进社会经济健康发展。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1986', '1992', '消费税中的卷烟、“白酒”实行复合计税办法。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1987', '1993', '设有两个以上机构并实行统一核算的纳税人，将货物从一个机构移送其他机构用于销售，属于视同销售行为，但相关机构设在同一县（市）的除外。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1988', '1994', '本题考核邮政业的范围，选项B属于迷惑选项。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1989', '1995', '选项C应由税务部门进行。', null, null, null, null, null, null, null);
