INSERT INTO `resolution` VALUES ('1991', '1997', '本题考核设立税务登记的相关规定。根据规定，已办理税务登记的扣缴义务人应当自扣缴义务发生之日起30日内，向税务登记地税务机关申报办理扣缴税款登记，税务机关不再核发扣缴税款登记证件。因此选项D的说法错误。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1992', '1998', '选项B，对职工个人以股份形式取得的企业量化资产参与企业分配而获得的股息、红利，应按“利息、股息、红利”项目征收个人所得税。选项C，实行内部退养的个人在办理内部退养手续后至法律规定离退休年龄之间从原任职单位取得的工资、薪金，不属于离退休工资，应按工资、薪金所得项目计征个人所得税。选项D，从事个体出租车运营的出租车司机取得的收入，按个体工商户的生产、经营所得计算缴纳个人所得税。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1993', '1999', '本题考核企业所得税的纳税地点。除税收法律、行政法规另有规定外，居民企业一般以企业登记注册地为纳税地点；但登记注册地在境外的，以实际管理机构所在地为纳税地点。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1994', '2000', '本题考核企业所得税的纳税义务人。个人独资企业属于个人所得税纳税义务人。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1995', '2001', '本题考核消费税的税率形式。消费税采用比例税率、定额税率和复合税率三种形式。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1996', '2002', '本题考核营业税税率。文化体育业适用税率为3%，服务业、金融保险业、销售不动产和转让无形资产适用5％的营业税税率。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1997', '2003', '本题考核营业税的纳税人。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1998', '2004', '本题考核增值税的类型。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('1999', '2005', '本题考核税收的特征。税收具有强制性、无偿性、固定性三个特征。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2000', '2006', '本题考核增值税的税率。电脑适用基本税率17%。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2001', '2007', '本题考核税务违法责任。税务违法责任包括行政法律责任和刑事法律责任。', null, null, null, null, null, null, null);
