INSERT INTO `resolution` VALUES ('2187', '2196', '本题考核政府采购方式。采用招标方式的，包括公开招标方式和邀请招标方式。两者的区别在于：公开招标方式是向不特定的供应商发出招标公告；邀请招标方式是向随机邀请的3家以上供应商发出招标邀请书，邀请其参加投标。', null, null, null, null, null, null, null);
