INSERT INTO `resolution` VALUES ('2189', '2198', '本题考查政府采购的功能。政府采购的功能包括（1）节约财政支出，提高采购资金的使用效益；（2）强化宏观调控；（3）活跃市场经济；（4）推进反腐倡廉；（5）保护民族产业。', null, null, null, null, null, null, null);
