INSERT INTO `resolution` VALUES ('2263', '2272', '邀请招标是指采购人或其委托的政府采购代理机构以投标邀请书的方式邀请三家或三家以上特定的供应商参与投票的采购方式。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2264', '2273', '本题考核政府采购方式。符合下列情形之一的货物或者服务，可以采用邀请招标方式采购：（1）具有特殊性，只能从有限范围的供应商处采购的；（2）采用公开招标方式的费用占政府采购项目总价值的比例过大的。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2265', '2274', '本题考核政府采购模式。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2266', '2275', '本题考核政府集中采购目录和采购限额标准的规定。政府集中采购目录和采购限额标准由省级以上人民政府确定并公布。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2267', '2276', '本题考核政府采购的资金范围。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2268', '2277', '本题考核《政府采购法》的适用范围。香港、澳门两个特别行政区的政府采购不适用《政府采购法》。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2269', '2278', '本题考核政府专门机构预决算的监督。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2270', '2279', '本题考核行政机关预决算的监督。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2271', '2280', '本题考核预算的执行。预算执行是指各级财政部门和其他预算主体组织预算收入和划拨预算支出的活动。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2272', '2281', '本题考核预算草案的概念。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2273', '2282', '本题考核全国人民代表大会常务委员的预算职权。撤销省、自治区、直辖市人民代表大会及其常务委员会制定的同宪法、法律和行政法规相抵触的关于预算、决算的地方性法规和决议，是全国人民代表大会常务委员会的职权之一。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2274', '2283', '本题考核国家预算的构成。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2275', '2284', '本题考核国家预算的级次划分。我国实行“一级政府，一级预算”，一共分为五级。包括中央，省、自治区、直辖市，设区的市、自治州，县、自治县、不设区的市、市辖区，乡、民族乡、镇五级预算。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2276', '2285', '本题考核预算法律制度。根据规定，不具备设立预算条件的乡、民族乡、镇，经省、自治区、直辖市政府确定，可以暂不设立预算。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2277', '2286', '本题考核预算的审批。根据规定，地方各级政府预算由本级人民代表大会审查和批准。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2278', '2287', '本题考核政府采购的监督检查。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2279', '2288', '本题考核政府采购当事人。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2280', '2290', '本题考核财政授权支付支出的范围。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2281', '2291', '本题考核预算管理职权。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2282', '2293', '属于中央预算的政府采购项目，其集中采购目录由国务院确定并公布。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2283', '2296', '世界各国采用的预算年度一般为一年，但起止时间不尽相同，我国采取的是“公历”年制。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2284', '2297', '中央预算支出是指按照分税制财政管理体制，由中央财政承担并列入中央预算的支出。包括中央本级支出和中央返还或者补助地方的支出。地方按规定上解中央的支出属于地方预算支出。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2285', '2298', '本题考核政府采购中采购人的内部监督。根据《政府采购法》规定，除涉及商业秘密外，政府采购的所有信息，都应当及时向社会公开发布。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2286', '2299', '本题考核政府采购的审批。根据规定，属于本单位有特殊要求的项目，经省级以上人民政府批准，可以自行采购。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2287', '2300', '本题考核政府采购的主体范围。目前，我国国有企业不属于政府采购的主体范围。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2288', '2301', '本题考查《政府采购法》，《政府采购法》是规范我国政府采购活动的根本性法律，也是制定其他政府法规制度的基本依据。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2289', '2302', '本题考核政府采购法。2002年6月29日第九届全国人大常委会第二十八次会议通过的《中华人民共和国政府采购法》是我国政府采购的主要法律依据。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2290', '2303', '本题考核决算草案的审批。乡、民族乡、镇政府编制本级决算草案，提请本级人民代表大会审查和批准。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2291', '2304', '本题考核预算收入的形式。我国《预算法》规定的预算收入形式包括：（1）税收收入。它是国家预算收入的最主要的部分。（2）依照规定应上缴的国有资产收益。例如依法应当上缴的国有资产投资产生的股息收入、国有资产的有偿转让、出让的收益等。（3）专项收入，即为了满足某种专门需要而有权收取的部门筹集的有专项用途的资金。例如铁道专项收入、征收排污费专项收入、电力建设基金专项收入。（4）其他收入。包括规费收入、罚没收', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2292', '2305', '本题考核预算收入的形式。税收收入是国家预算收入的最主要部分。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2293', '2306', '本题考核国家预算的职能。作为财政分配和宏观调控的主要手段，国家预算具有分配、调控和监督职能。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2294', '2307', '本题考核国家预算的概念。国家预算也称政府预算，是指经过法定程序批准的国家年度财政收支计划。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2295', '2308', '本题考核国家预算的作用。我国国家预算的作用包括财力保证作用、调节制约作用和反映监督作用。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2296', '2309', '本题考核国家预算的作用。国家预算是国民经济的综合反映，预算收入反映国民经济发展规模和经济效益水平，预算支出反映各项建设事业发展的基本情况。因此通过国家预算的编制和执行便于掌握国民经济的运行状况、发展趋势以及出现的问题，从而采取对策措施，促进国民经济稳定协调地发展。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2297', '2310', '本题考核预算法。预算法是我国第一部财政基本法律，是我国国家预算管理工作的根本性法律以及制定其他预算法规的基本依据。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2298', '2311', '本题考核预算法的通过时间。现行的预算法为1994年3月22日第八届全国人民代表大会第二次会议通过《中华人民共和国预算法》。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2299', '2312', '本题考核财政授权支付程序的概念。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2300', '2313', '本题考核财政支出支付方式。财政直接支付是指由财政部向中国人民银行和代理银行签发支付指令，代理银行根据支付指令通过国库单一账户体系将资金直接支付到收款人或用款单位账户。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2301', '2314', '本题考核国库单一账户体系的构成。财政部门开设的零余额账户，营业中单笔支付额在5000万元人民币以上（含5000万元）的，应当及时与国库单一账户清算。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2302', '2315', '本题考核国库集中收付制度。国库集中收付制度一般也称为国库单一账户制度，包括国库集中支付制度和收入收缴管理制度。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2303', '2316', '本题考核小额现金账户的用途。小额现金账户是财政部门为预算单位在代理银行开设的，用于记录、核算和反映预算单位的小额零星支出,并与国库单一账户进行清算。设立此类账户主要是方便预算单位日常发生的一些零星分散、数额小、支付频繁的支出。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2304', '2317', '本题考核采购方式。邀请招标是指采购人依法从符合相应资格条件的供应商中随机邀请3家以上的供应商，并以投标邀请书的方式，邀请其参加投标。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2305', '2318', '选项C公开招标是政府采购的主要采购方式。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2306', '2319', '政府采购中的集中采购的范围由省级以上人民政府公布的集中采购目录确定。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2307', '2320', '本题考核集中采购目录。属于中央预算的政府采购项目，其集中采购目录和政府采购限额标准由国务院确定并公布；属于地方预算的政府采购项目，其集中采购目录和政府采购限额标准由省、自治区、直辖市人民政府或者其授权的机构确定并公布。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2308', '2321', '本题考核政府采购的原则。选项A体现的是“诚实信用原则”；选项B体现的是“公平竞争原则”；选项D体现的“公正原则”。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2309', '2322', '本题考核政府采购的原则。政府采购原则包括公开透明原则、公平竞争原则、公正原则、诚实信用原则。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2310', '2323', '本题考核预决算的监督。根据规定，预决算的监督按照时间先后不同分为事前监督、事中监督和事后监督。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2311', '2324', '本题考核预决算监督的相关规定。各级审计机关应当依照《中华人民共和国审计法》以及有关法律、行政法规的规定，对本级预算执行情况，对本级各部门和下级政府预算的执行情况和决算进行审计监督。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2312', '2325', '本题考核预算支出的相关规定。各部门、各单位的预算支出，必须按照本级政府财政部门批复的预算科目和数额执行，不得挪用；确需作出调整的，必须经本级政府财政部门同意。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2313', '2326', '本题考核全国人民代表大会的预算职权。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2314', '2327', '本题考核全国人民代表大会的预算职权。监督中央预算和中央预算执行情况的报告属于全国人民代表大会常务委员会的职权。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2315', '2328', '中央预算的编制内容包括（一）本级预算收入和支出；（二）上一年度结余用于本年度安排的支出；（三）返还或者补助地方的支出；（四）地方上解的收入。 中央财政本年度举借的国内外债务和还本付息数额应当在本级预算中单独列示。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2316', '2329', '各部门对所属各单位的决算草案，应当审核并汇总编制本部门的决算草案，在规定的期限内报本级政府财政部门审核。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2317', '2330', '本题考核国家预算的组成。选项C，国家预算按照收支管理范围可分为总预算和部门预算', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2318', '2331', '本题考核预算体系组成。我国的预算组成并非没有限制的，中央预算由中央各部门（含直属单位）的预算组成。地方预算由各省、自治区、直辖市预算组成。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2319', '2332', '本题考核国家预算的构成。中央预算由中央各部门（含直属单位）的预算组成。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2320', '2333', '本题考核预算的相关规定。我国国家预算共分为五级。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2321', '2334', '谈判小组由采购人的代表和有关专家共三人以上的单数组成，其中专家的人数不得少于成员总数的三分之二。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2322', '2335', '根据政府采购的上述定义，政府采购资金为财政性资金。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2323', '2336', '根据《政府采购法》的规定，集中采购必须委托采购机构代理采购。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2324', '2337', '各预算活动的主体都必须按照法律规定的时间要求及时地编制预算，各级政府、各部门、各单位应当按照国务院规定的时间编制预算草案。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2325', '2338', '本题考核专项收入的概念。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2326', '2339', '本题考核供应商的权利，选项A是供应商的义务。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2327', '2340', '本题考核政府采购监督管理部门的监督。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2328', '2341', '本题考核政府采购的监督检查。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2329', '2342', '政府采购当事人是指在政府采购活动中享有权利和承担义务的各类主体，包括采购人、供应商和采购代理机构。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2330', '2343', '本题考核政府采购人的范围。根据规定，采购人是指依法进行政府采购的国家机关、事业单位和团体组织。人民教育出版社现已改制成企业，不属于采购人。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2331', '2344', '本题考查政府采购法律制度的构成，政府采购法律制度包括政府采购法、政府采购部门规章、政府采购地方性法规和政府规章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2332', '2345', '本题考核编制决算草案原则。编制决算草案原则是：合法原则、准确完整原则、报送及时原则。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2333', '2346', '本题考核预算收入的形式。我国的预算收入形式包括税收收入、依规定应上缴的国有资产收益、专项收入和其他收入。其他收入包括规费收入、罚没收入等。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2334', '2347', '本题考核我国预算支出的形式。我国预算支出包括：（1）经济建设支出；（2）教育、科学、文化、卫生、体育等事业发展支出；（3）国家管理费用支出；（4）国防支出；（5）各项补贴支出；（6）其他支出。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2335', '2348', '本题考核国家预算的职能。国家预算作为财政分配和宏观调控的主要手段，具有分配、调控和监督职能。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2336', '2349', '本题考核国家预算的作用。国家预算的收支规模可调节社会总供给和总需求的平衡，预算支出的结构可调节国民经济结构体现的是调节制约作用。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2337', '2350', '本题考核预算的概念和分类。预算，指国家预算，它是国家对会计年度内的收入和支出的预算结算，包括中央预算和地方预算。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2338', '2351', '本题考核《预算法》的制定宗旨。《预算法》是我国财政法规范性文件体系中的一部至为重要的法律，其宗旨是强化预算的分配和监督职能，健全国家对预算的管理，健全国家宏观调控，保障经济和社会的健康发展。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2339', '2352', '本题考核财政支出支付方式。财政性资金的支付实行财政直接支付和财政授权支付两种方式。', null, null, null, null, null, null, null);
