INSERT INTO `resolution` VALUES ('2413', '2430', '本题考核会计职业道德教育的形式。应大力提倡会计人员进行自我教育，在实践中不断地加强职业道德修养，养成良好的道德行为，从而实现道德境界的升华。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2414', '2431', '本题考核会计职业道德教育的形式。从会计人员的角度讲，会计职业道德教育的主要形式包括接受教育和自我教育两种。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2415', '2432', '本题考核强化服务的主要内容。强化会计职业服务的基本要求就是会计人员要有强烈的服务意识，服务要文明，质量要上乘。', null, null, null, null, null, null, null);
