INSERT INTO `resolution` VALUES ('2417', '2434', '财务部经理李某的行为符合参与管理的会计职业道德。参与管理要求会计人员在做好本职工作的同时，努力钻研相关业务，全面熟悉经营活动和业务流程，主动提出合理化建议等。', null, null, null, null, null, null, null);
