INSERT INTO `resolution` VALUES ('2421', '2438', '本题考核提高技能的内容。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2422', '2439', '本题考核提高技能的内容。职业技能，也可称为职业能力，是人们进行职业活动，承担职业责任的能力和手段。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2423', '2440', '本题考核坚持准则。坚持准则就是要求会计人员熟悉国家法律、法规和国家统一的会计制度，始终按照法律、法规和国家统一的会计制度的要求实行会计核算。这里的准则不仅是指会计准则，而且还包括会计法律、会计行政法规、国家统一的会计制度以及与会计工作相关的法律制度。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2424', '2441', '本题考核坚持准则。这句话体现了会计职业道德要求中的“坚持准则”。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2425', '2442', '本题考核会计职业道德的主要内容。会计人员应按照国家统一的会计制度的规定记账、算账、报账，如实反映单位的经济活动情况，该表述符合会计人员坚持准则、客观公正的职业道德要求。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2426', '2443', '本题考核客观公正的基本要求。依法办事，认真遵守法律法规，是会计工作保证客观公正的前提。', null, null, null, null, null, null, null);
