INSERT INTO `resolution` VALUES ('2603', '2620', '会计职业道德与会计法律制度两者有着共同的目标、相同的调整对象、承担着同样的责任，两者联系密切。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2604', '2621', '本题考核会计职业道德的概念。 会计职业道德是指在会计职业活动中应当遵循的、体现会计职业特征的调整会计职业关系的职业行为准则和规范。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2605', '2622', '本题考核会计职业道德与会计法律制度的关系。会计职业道德与会计法律制度的主要区别是性质不同、作用范围不同、表现形式不同和实施保障机制不同。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2606', '2623', '本题考核会计职业道德的内容。会计人员不是无条件的服从领导，需要坚持准则。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2607', '2624', '本题考核会计职业道德教育的途径。 会计职业道德教育途径包括：（1）通过会计学历教育进行会计职业道德教育；（2）通过会计继续教育进行会计职业道德教育；（3）通过会计人员的自我教育与修养进行会计职业道德教育。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2608', '2625', '本题考核会计职业道德的组织实施。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2609', '2626', '本题考核国家预算的作用。 国家预算的作用包括财力保证作用、调节制约作用和反映监督作用。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2610', '2627', '本题考核国家预算的级次划分。我国的国家预算实行一级政府一级预算，共分为五级预算，具体包括：中央预算；省级（省、自治区、直辖市）预算；地方及预算（设区的市、自治州）预算；县市级（子安、自治县、不设区的市、市辖区）预算；乡镇级（乡、民族乡、镇）预算。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2611', '2628', '本题考核国家预算的概念。我国的预算收入采用税收等形式，而不是无偿划拨的形式。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2612', '2629', '本题考核部门预算的相关内容。编制本单位预算、决算草案是各单位的职权。所以各单位预算草案由本单位负责编制。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2613', '2630', '本题考核国家预算的级次划分依据。我国国家预算级次结构设计依据是国家政权结构、行政区域划分和财政管理体制要求而确定的国家预算组织结构。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2614', '2631', '本题考核零余额账户的使用。预算单位零余额账户可以办理转账、提取现金等结算业务，可以向本单位按账户管理规定保留的相应账户划拨工会经费、住房公积金及提租补贴，以及经财政部门批准的特殊款项，不得违反规定向本单位其他账户和上级主管单位、所属下级单位账户划拨资金。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2615', '2632', '本题考核增值税进项税的抵扣。可抵扣的进项税＝（100 000×17%＋500×7%）÷10 000＝1.7035（万元）。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2616', '2633', '本题考核进项税的抵扣。向供销公司购进棉花可抵扣的进项税额＝1.5×30×13%＝5.85（万元）。增值税一般纳税人向农产品生产者购买免税农产品，准予按收购凭证上注明的收购价和13%的扣除率计算进项税额，从当期销项税额中扣除。可抵扣的进项税额＝14×13%＝1.82（万元）。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2617', '2634', '本题考核销项税的确定。本月销售货物应确认的销项税额＝40×1.2×17%＋50×2.34÷（1+17%）×17%＝25.16（万元）。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2618', '2635', '本题考核增值税的计算。本月应缴纳的增值税＝25.16－1.7035－1.82－5.85＝15.7865（万元）。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2619', '2636', '本题考核增值税的纳税期限。增值税的纳税期限分为为1日、3日、5日、10日、15日、1个月或者1个季度。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2620', '2637', '本题考核税务登记。从事生产、经营的纳税人，应当自领取营业执照之日起30日内，向生产、经营地或者纳税义务发生地的主管税务机关申报办理税务登记。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2621', '2638', '本题考核会计法律制度中关于会计记录使用文字的规定。根据规定，在中国境内的外商投资企业，会计记录文字应当使用中文，同时可以选择一种外文。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2622', '2639', '本题考核会计法律制度中关于会计记录使用文字的规定。根据规定，业务收入以人民币以外的货币为主的单位，可以选定其中一种货币作为记账本位币，但是编报的财务会计报告应当折算成人民币。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2623', '2640', '本题考核会计法律制度对于财务会计报告上签章人员的规定。根据规定：对外提供的财务会计报告必须由单位负责人和主管会计工作的负责人、会计机构负责人（会计主管人员）签名并盖章；设置总会计师的单位，还须由总会计师签名并盖章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2624', '2641', '本题考核会计法律制度对于财务会计报告上签章人员的规定。单位负责人、主管会计工作的负责人、会计机构负责人（会计主管人员）在财务会计报告上签章是签名并盖章。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2625', '2642', '本题考核会计法律制度对会计凭证的相关要求。原始凭证有错误的，应当由出具单位重开或更正，更正处应当加盖出具单位印章；原始凭证金额有错误的，应当由出具单位重开，不得在原始凭证上更正。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2626', '2643', '本题考核会计人员回避制度。国家机关、国有企业、事业单位任用会计人员应当实行回避制度。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2627', '2644', '本题考核会计人员回避制度。需要回避的直系亲属：夫妻关系、直系血亲关系、三代以内旁系血亲以及配偶亲关系。小江属于三代以内旁系血亲，应当回避，不能在本单位担任出纳。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2628', '2645', '本题考核会计人员的工作交接。一般会计人员交接，由单位会计机构负责人、会计主管人员负责监交。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2629', '2646', '本题考核会计人员的工作交接。根据规定：接替人员应当继续使用移交的会计账簿，不得自行另立新账，以保持会计记录的连续性。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2630', '2647', '本题考核会计凭证的相关内容。原始凭证有错误的，应当由出具单位重开或更正，更正处应当加盖出具单位印章；原始凭证金额有错误的，应当由出具单位重开，不得在原始凭证上更正。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2631', '2648', '本题考核票据的基本当事人。支票的出票人是签发支票的单位或个人，付款人是出票人的开户银行。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2632', '2649', '本题考核签发支票的要求。选项A应该有出票人签章；选项D应该用碳素墨水。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2633', '2650', '本题考核签发支票的要求。出票人签发空头支票、签章与预留签章不符或使用支付密码错误的支票，银行应予以退票，并按票面金额处以5%但不低于1 000元的罚款，持票人有权要求出票人赔偿支票金额2%的赔偿金。16 000×5％＝800（元）＜1 000元，所以银行有权要求出票人的赔偿金额为1 000元。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2634', '2651', '本题考核支票的分类。支票按照支付票款方式的不同，分为现金支票、转账支票和普通支票三类。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2635', '2652', '本题考核支票的种类。现金支票只能用于支取现金，不能用于转账；转账支票只能用于转账，不能支取现金；普通支票可以支取现金，也可以用于转账；划线支票只能用于转账，不得支取现金。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2636', '2653', '本题考核汇票与支票的区别。 我国《票据法》和《支付结算办法》规定了两项绝对记载事项可以通过授权补记的方式记载，包括：支票的金额、收款人名称，可以 有出票人授权补记。未补记前不得背书转让和提示付款。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2637', '2654', '本题考核填写票据的基本要求。我国《票据法》和《支付结算办法》规定票据的出票日期必须使用中文大写。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2638', '2655', '本题考核支票办理要求。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2639', '2656', '本题考核支票。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2640', '2657', '本题考核支票。在票据上不得更改的项目是金额、出票日期和收款人名称，付款人名称是可以更改的。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2641', '2658', '本题考核增值税的计算。850000×17%+42120÷（1+17%）×17%+20000×17%=154020（元）。 ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2642', '2659', '本题考核增值税的计算。580000×13%+21760+1000×7%+18000×17%=', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2643', '2660', '本题考核增值税的计算。当期应纳税额=当期销项税额—当期进项税额=154020—100290=53730（元） ', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2644', '2661', '本题考核增值税的分类。 增值税分为生产型增值税、收入型增值税、消费型增值税三种类型。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2645', '2662', '本题考核增值税的纳税期限。 增值税的纳税期限分别为1日、3日、5日、10日、15日、1个月或者1个季度。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2646', '2663', '此款咖啡的特点是在热咖啡中添加水果甜酒，以产生迷人的风味，故咖啡的量不易太多，否则会掩盖风味。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2647', '2664', '这款咖啡因为生姜的原因，会起到一定的驱寒作用，同时有咖啡的作用，让人精力比较充足。生姜加入太多会影响咖啡口味。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2648', '2665', '花式冰咖啡因其优雅的造型，清爽的口感，倍受欢迎。碎冰与咖啡的融合，不仅能带来美味的口感，也易造型。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2649', '2666', '此款口感以仍以咖啡为主，加上汽水会产生啤酒般的泡沫，具有啤酒般的幻觉和享受，却比啤酒更香醇美味。特色：感觉有啤酒的颜色与泡沫，是一种具模拟性的特调冰咖啡。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2650', '2667', '牙买加特产的棕兰姆酒，味醇而浓烈，后劲足，能满足口味重的人士，畅饮之后使人神清气爽、精神百倍！ 需7分满冰块调制。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2651', '2668', '此款配方需泡一杯红茶，加入打发好的奶泡，后以1勺木瓜粉调味。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2652', '2669', '此款的特点喝起来首先给人是一种冰爽的快感，解渴的威力不亚于冰冻的酸梅汤；而冰爽之中潜藏其间的茶之芳香依旧向味蕾袭来。制作时用不锈钢的调酒器，置入一茶匙的糖水，再冲入8分满的热茶水，后加入冰块，猛烈地摇动着容器，产生泡沫浮于杯面。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2653', '2670', '奶茶和珍珠奶茶果粉的配比多少，直接影响到珍珠奶茶的口感、质量，一旦过量，会使奶茶的味道变得有些腻口。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2654', '2671', '芋热奶茶配方：香芋粉１６克、奶精粉８克、红茶包二个、蜂蜜适量。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2655', '2672', '奶泡制作时温度很重要，温度过低打不出细腻的奶泡，温度过高会破坏牛奶中的蛋白质结构，影响口感和质量。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2656', '2673', '京式面点的代表性品种有龙须面、抻面、山西刀削面、银丝卷、北京都一处烧麦、天津狗不理包子、清朝宫廷仿膳肉末烧饼、窝窝头等。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2657', '2674', '水磨粉制作方法：糯米浸泡一夜，水磨打成浆水，用个布袋装着吊一个晚上，待水滴干了，把湿的糯米粉团掰碎晾干。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2658', '2675', '杂菌繁殖的温度是40度，过高过低温度都不适用。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2659', '2676', '冷水面团：是指用30℃以下的水与面粉直接拌和、揉搓而成的面团。 温水面团: 是指用50℃的水与面粉直接拌和、揉搓而成的面团。或者是指用一部分沸水先将面粉调成雪花面，再淋上冷水拌和、揉搓而成的面团。热水面团：用70度以上的热水调制而成的面团。又称开水面团。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2660', '2677', '清朝，是我国面点技术发展的鼎盛时期，出现了以面点为主的筵席。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2661', '2678', '糖具有渗透性，可以使面团劲性降低。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2662', '2679', '淀粉糊就是用淀粉与水拌制而成的，制作简单方便，应用广，多用于干炸、焦、熘、抓炒等烹调方法。制成的菜色金黄、外脆硬、内鲜嫩，如：干炸里脊、抓炒鱼块等。', null, null, null, null, null, null, null);
