INSERT INTO `resolution` VALUES ('2664', '2681', '麦穗形花刀是一种加工食材的刀法，可以用于加工腰子、鱿鱼等。荔枝形花刀，剞法与麦穗形花刀相同。只是在原料形状改刀时改成象眼块（菱形块）加热后就卷曲成荔枝形。例如：腰子、鱿鱼等。', null, null, null, null, null, null, null);
