INSERT INTO `resolution` VALUES ('2685', '2702', '在酿造君度酒的法国西部昂热地区，人们习惯兑在咖啡里一起饮用:热咖啡150毫升、君度酒10毫升、鲜奶油适量、菠萝1片 。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2686', '2703', '本配方中的热奶泡温度要求和其他配方中的奶泡一样，50度是打发奶泡的最佳温度。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2687', '2704', '卡布奇诺，是一种加入以同量的意大利特浓咖啡和蒸汽泡沫牛奶相混合的意大利咖啡。冰咖啡是杯子里放入7分满碎冰，加上打成泡沫的砂糖甜牛乳。其上缓缓地倒尽冷的咖啡。如此，牛乳与咖啡分成二层，牛乳的泡沫浮在最上层。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2688', '2705', '卡布奇诺，是一种加入以同量的意大利特浓咖啡和蒸汽泡沫牛奶相混合的意大利咖啡。冰咖啡是杯子里放入7分满碎冰，加上打成泡沫的砂糖甜牛乳。其上缓缓地倒尽冷的咖啡。如此，牛乳与咖啡分成二层，牛乳的泡沫浮在最上层。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2689', '2706', '此款咖啡需加入8分满碎冰，特色是上下两色自然形成的花纹，犹如午夜的香吻，梦幻又真实。', null, null, null, null, null, null, null);
