INSERT INTO `resolution` VALUES ('2700', '2717', '此款配方需6分满碎冰，利用果糖与牛奶混合增加牛奶的比重，使它与比重较轻的咖啡不会混合，成为黑白分明的两层，形成如鸡尾酒般曼妙的视觉效果，再加上冰块，给人一种高雅而浪漫的温馨感觉。', null, null, null, null, null, null, null);
