INSERT INTO `resolution` VALUES ('2718', '2735', '由于小苏打热稳定性较差,加热至140摄氏度左右就会分解,生成碳酸钠、水和二氧化碳。二氧化碳会从面中逸出,使面食中有一个一个小孔,这样,增大体积,使得面食松软。', null, null, null, null, null, null, null);
INSERT INTO `resolution` VALUES ('2719', '2736', '只用冷水拌和面粉调制的面团，称为冷水面团。特点是：结构紧密韧性强，做出的成品色白，爽口有劲。调制冷水面团，冬天使用的热水不能超过40度。温度太高面粉粘连影响强度和筋力。', null, null, null, null, null, null, null);
