/* Function  structure for function  `createSeq` */

/*!50003 DROP FUNCTION IF EXISTS `createSeq` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `createSeq`(seq_name VARCHAR(50),current_value int(11),increment int(11)) RETURNS int(11)
BEGIN
INSERT INTO t_sequence (name,current_value,increment) VALUES (seq_name,current_value,increment); 
RETURN currval(seq_name);
END */$$
DELIMITER ;

/* Function  structure for function  `currval` */

/*!50003 DROP FUNCTION IF EXISTS `currval` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `currval`(seq_name VARCHAR(50)) RETURNS int(11)
BEGIN
  DECLARE value INTEGER;
  SET value = 0;
  SELECT current_value INTO value
  FROM t_sequence
  WHERE name = seq_name;
  RETURN value;
END */$$
DELIMITER ;

/* Function  structure for function  `nextval` */

/*!50003 DROP FUNCTION IF EXISTS `nextval` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `nextval`(seq_name VARCHAR(50)) RETURNS int(11)
BEGIN
UPDATE t_sequence
SET current_value = current_value + increment
WHERE name = seq_name;
RETURN currval(seq_name);
END */$$
DELIMITER ;

/* Function  structure for function  `setval` */

/*!50003 DROP FUNCTION IF EXISTS `setval` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `setval`(seq_name VARCHAR(50), value INTEGER) RETURNS int(11)
BEGIN
UPDATE t_sequence
SET current_value = value
WHERE name = seq_name;
RETURN currval(seq_name);
END */$$
DELIMITER ;

